mapper.update:
update member
set member_token = #{token}
where member_no = #{member_no}

update member
set member_token = #{token, jdbcType=VARCHAR}
where member_no = #{member_no}

update member
set member_name=#{member_name},member_email=#{member_email}, member_nickname=#{member_nickname}
where member_no=#{member_no}

update member
set member_deleted=1
where member_no=#{member_no}
